const getSize = require('get-folder-size');
const exec = require('child_process').execFile;

const getOnUnixLike = (dir) => {
  const regex = /^(\d*)/;

  return new Promise((resolve, reject) => {
    exec('du', ['-ks', dir], (err, stdout, stderr) => {
      const matches = stdout.match(regex);
      const kbytes = matches[1];
      const bytes = kbytes * 1024;
      if (!err && !stderr && matches && matches.length > 1) {
        resolve(bytes);
      } else {
        reject(err, stderr, stdout);
      }
    });
  });
};

const getOnWindows = (dir) => {
  return new Promise((resolve, reject) => {
    getSize(dir, (err, size) => {
      if (err) {
        return reject(err);
      }

      return resolve(size);
    });
  });
};

module.exports = process.platform === 'win32' ? getOnWindows : getOnUnixLike;
