/* eslint-disable import/no-extraneous-dependencies */
/* eslint no-console:"off" */

/**
 * This script demonstrates how to transfer tokens between 2 accounts
 *
 * Run script with: `DEBUG=@ocap/client node examples/transfer_token.js`
 */

const GraphqlClient = require('@ocap/client');
const { fromRandom } = require('@ocap/wallet');

const endpoint = process.env.OCAP_API_HOST || 'http://127.0.0.1:4000'; // testnet

const client = new GraphqlClient(`${endpoint}/api`);
const sleep = (timeout) =>
  new Promise((resolve) => {
    setTimeout(resolve, timeout);
  });

(async () => {
  try {
    const sender = fromRandom();
    const receiver = fromRandom();
    console.log({ sender: sender.address, receiver: receiver.address });

    // 1. Declare sender
    let hash = await client.declare({ moniker: 'sender', wallet: sender });
    console.log('sender declare tx', `${endpoint}/explorer/txs/${hash}`);
    console.log('sender account', `${endpoint}/explorer/accounts/${sender.address}`);

    // 2. Declare receiver
    hash = await client.declare({ moniker: 'receiver', wallet: receiver });
    console.log('receiver declare tx', `${endpoint}/explorer/txs/${hash}`);
    console.log('receiver account', `${endpoint}/explorer/accounts/${receiver.address}`);

    await sleep(3000);

    // eslint-disable-next-line prefer-const
    const createTokenResult = await client.createToken({
      name: 'demo token',
      description: 'demo token',
      symbol: `DT_${Math.floor(Math.random() * 10000)}`,
      unit: 'D',
      decimal: 8,
      totalSupply: 1000000000000,
      wallet: sender,
    });

    await sleep(3000);
    console.log('create token result', createTokenResult);

    // 5. Transfer token
    hash = await client.transfer({
      to: receiver.address,
      tokens: [{ address: createTokenResult[1], value: 1.1 }],
      memo: 'this is the transfer secondary note',
      wallet: sender,
    });
    console.log('view transfer tx', `${endpoint}/explorer/txs/${hash}`);
  } catch (err) {
    console.error(err);
    console.log(JSON.stringify(err.errors));
  }
})();
